class UserMode::SamlAuthStrategy
  def set_auth_provider(omniauth)
    omniauth.provider :saml,
      :assertion_consumer_service_url     => "http://central.localmumuki.io:3000/auth/saml/callback",
      :issuer                             => "Mumuki",
      :idp_sso_target_url                 => "http://localhost:9999/simplesaml/saml2/idp/SSOService.php",
      :idp_cert                           => "-----BEGIN CERTIFICATE-----\\n-----END CERTIFICATE-----"
  end

  def init_partial
    'layouts/auth_partials/null_partial.html.erb'
  end

  def html_badge
    ''
  end

  def should_be_forgery_protected?
    # The IdP calls the assertion_url via POST and without the CSRF token
    false
  end

  def auth_link
    'href="http://central.localmumuki.io:3000/auth/saml/"'
  end

end